import std/setutils

let input = "";

proc findFirstDistinct4(data: string): int =
  for i in 3 ..< len(data):
    if data[i] == data[i - 1] or data[i] == data[i - 2] or data[i] == data[i - 3]:
      continue
    if data[i - 1] == data[i - 2] or data[i - 1] == data[i - 3]:
      continue
    if data[i - 2] == data[i - 3]:
      continue
    return i + 1
  raise

proc findFirstDistinctN(data: string, n: int): int =
  for i in n-1 ..< len(data):
    let chars = toSet(data[i-n+1 .. i])
    if len(chars) == n:
      return i + 1
  raise

assert findFirstDistinct4("mjqjpqmgbljsphdztnvjfqwrcgsmlb") == 7
assert findFirstDistinct4("bvwbjplbgvbhsrlpgdmjqwftvncz") == 5
assert findFirstDistinct4("nppdvjthqldpwncqszvftbrmjlhg") == 6
assert findFirstDistinct4("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg") == 10
assert findFirstDistinct4("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw") == 11
echo findFirstDistinct4(input)

assert findFirstDistinctN("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 14) == 19
assert findFirstDistinctN("bvwbjplbgvbhsrlpgdmjqwftvncz", 14) == 23
assert findFirstDistinctN("nppdvjthqldpwncqszvftbrmjlhg", 14) == 23
assert findFirstDistinctN("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 14) == 29
assert findFirstDistinctN("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 14) == 26
echo findFirstDistinctN(input, 14)
